jQuery(window).on("elementor/frontend/init",()=>{const slider=jQuery(".review-cards");slider.slick({infinite:true,draggable:false,autoplay:true,dots:true,arrows:false,autoplaySpeed:2e3,slidesToShow:1,adaptiveHeight:true,prevArrow:`<button class="slick-arrow --prev"></button>`,nextArrow:`<button class="slick-arrow --next"></button>`,mobileFirst:true,responsive:[{breakpoint:1024,settings:{slidesToShow:3,dots:false,arrows:true}},{breakpoint:900,settings:{slidesToShow:2,dots:false,arrows:true}},{breakpoint:576,settings:{slidesToShow:2}}]});const colors=["#ab47bc","#00897b","#8d6e63","#ea4335","#689f38"];function getRandomColor(){return colors[Math.floor(Math.random()*colors.length)]}const boxes=document.querySelectorAll(".initial");boxes.forEach(box=>{box.style.backgroundColor=getRandomColor()});const extendButtons=jQuery(".extend-button");const reduceButtons=jQuery(".reduce-button");extendButtons.on("click",function(){const parentCard=jQuery(this).closest(".review-card");const reviewTextElement=parentCard.find(".review-text");const textElementHeight=reviewTextElement[0].scrollHeight+10;reviewTextElement.css("height",`${textElementHeight}px`);jQuery(this).addClass("--hidden");parentCard.find(".reduce-button").removeClass("--hidden")});reduceButtons.on("click",function(){const parentCard=jQuery(this).closest(".review-card");const reviewTextElement=parentCard.find(".review-text");reviewTextElement.css("height","130px");jQuery(this).addClass("--hidden");parentCard.find(".extend-button").removeClass("--hidden")})});